(152, 0, 0, 0, 'chaj-fruktovyj-julius-kir-royal-250-g', 'Чай фруктовый Julius Meinl Кир Роял 250 г', 'JM034', 'Julius Meinl', '', '<p style="text-align: justify;"><span style="font-size: 18pt; color: #8c5a2a;">Чай фруктовый Julius Meinl Кир Роял 250 г</span></p>\r\n<p style="text-align: justify;">Названный в честь одноименного алкогольного коктейля на основе черносмородинового ликера, Кир Ройял &ndash; это яркое, душистое, фруктово &ndash; ягодное сочетание с искристым вкусом и слегка вяжущим послевкусием. Состав: цветы гибискуса, кусочки папайи, черника, ягоды бузины, кусочки клубники и малины.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:18:"фруктовый";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"250";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 396, 0, 0, 'a:1:{i:0;s:21:"1486107594_JM_14.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай фруктовый Julius Meinl Кир Роял 250 г', '', '', '2017-02-03 07:38:02', '2017-02-18 15:06:24'),
(153, 0, 0, 0, 'chaj-zelenyj-julius-meinl-kitajskij-zelenyj-drakon-100-g', 'Чай зеленый Julius Meinl Китайский зеленый дракон 100 г', 'JM053', 'Julius Meinl', '', '<p><span style="font-size: 18pt; color: #8c5c1c;">Чай зеленый Julius Meinl Китайский зеленый дракон 100 г</span></p>\r\n<p style="text-align: justify;">Регион сбора: Китай, провинция Юньнань.</p>\r\n<p style="text-align: justify;">Изящно изогнутые чаинки дымчато-зеленого оттенка несут мягкий аромат и весьма насыщенный для зеленого чая, вкус. Чай из провинции Юньнань отличается особой яркостью и это не случайно. Юньнань &ndash; родина чайного растения &ndash; камелии китайской, именно здесь несколько тысяч лет назад было обнаружено чайное дерево.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:14:"зеленый";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"100";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 159, 0, 0, 'a:1:{i:0;s:21:"1486107870_JM_15.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай зеленый Julius Meinl Китайский зеленый дракон 100 г', '', '', '2017-02-03 07:44:07', '2017-02-18 09:52:15'),
(154, 0, 0, 0, 'chaj-zelenyj-julius-meinl-zhasmin-kitajskij-250-g', 'Чай зеленый Julius Meinl Жасмин Китайский 250 г', 'JM060', 'Julius Meinl', '', '<p><span style="font-size: 18pt; color: #9c6519;">Чай зеленый Julius Meinl Жасмин Китайский 250 г</span></p>\r\n<p>Регион сбора: Китай.</p>\r\n<p>Сочетание чайного листа с цветами жасмина &ndash; один из древнейших способов натуральной ароматизации чая. Жасмин, благодаря охлаждающим свойствам идеален в сочетании с зеленым чаем. Прозрачно-золотистый настой хорошо освежает и утоляет жажду, не требует длительного&nbsp; настаивания.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:14:"зеленый";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"250";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 396, 0, 0, 'a:1:{i:0;s:21:"1486108042_JM_12.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай зеленый Julius Meinl Жасмин Китайский 250 г', '', '', '2017-02-03 07:46:56', '2017-02-18 09:55:58'),
(155, 0, 0, 0, 'chaj-ulun-julius-meinl-molochnyj-ulun-100-g', 'Чай улун Julius Meinl Молочный Улун 100 г', 'JM056', 'Julius Meinl', '', '<p style="text-align: justify;"><span style="font-size: 18pt; color: #9e6f16;">Чай улун Julius Meinl Молочный Улун 100 г</span></p>\r\n<p style="text-align: justify;">Регион сбора: Китай, о.Тайвань</p>\r\n<p style="text-align: justify;">Молочный улун впервые был придуман на острове Тайвань. Тугие, плотно скрученные чаинки улунского чая слабой ферментации ароматизируют молочным экстрактом, благодаря чему природная мягкость и сладость сорта усиливается, приобретая ярко &ndash; выраженное сливочное послевкусие.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:8:"улун";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"100";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 225, 0, 0, 'a:1:{i:0;s:21:"1486108437_JM_15.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай улун Julius Meinl Молочный Улун 100 г', '', '', '2017-02-03 07:53:25', '2017-02-18 15:45:42'),
(156, 0, 0, 0, 'chaj-zelenyj-julius-meinl-ganpauder-100-g', 'Чай зеленый Julius Meinl Ганпаудер 100 г', 'JM058', 'Julius Meinl', '', '<p><span style="font-size: 18pt;">Чай зеленый Julius Meinl Ганпаудер 100 г</span></p>\r\n<p style="text-align: justify;">Регион сбора: Китай, провинция Чжэцзянь.</p>\r\n<p style="text-align: justify;">Ганпаудер скручен по технологии приготовления улуна слабой ферментации, что позволяет подчеркнуть наиболее яркие оттенки вкуса зеленого чая. Нежный, табачно-зеленый настой дарит насыщенный, терпкий, слегка горьковатый вкус.&nbsp; Аромат несет сладко-дымные ноты.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:14:"зеленый";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"100";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 159, 0, 0, 'a:1:{i:0;s:21:"1486108750_JM_16.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай зеленый Julius Meinl Ганпаудер 100 г', '', '', '2017-02-03 07:58:41', '2017-02-18 09:58:36'),
(157, 0, 0, 0, 'chaj-zelenyj-julius-meinl-solnechnaya-sencha-100-g', 'Чай зеленый Julius Meinl Солнечная Сенча 100 г', 'JM053', 'Julius Meinl', '', '<p style="text-align: justify;"><span style="color: #8c6315; font-size: 18pt;">Чай зеленый Julius Meinl Солнечная Сенча 100 г</span></p>\r\n<p style="text-align: justify;">Сенча &ndash; всемирно известный сорт зеленого чая, сделанный&nbsp;по японской технологии пропаривания. Изумрудные, скрученные в тонкие, продолговатые трубочки, чайные листочки изящно раскрываются при заваривании, имеют мягкий, сбалансированный вкус и несут сладкий, специфический аромат, напоминающий свежесть морского бриза.</p>', '', '', 'a:5:{i:0;a:2:{s:4:"name";s:18:"Категория";s:5:"value";s:14:"зеленый";}i:1;a:2:{s:4:"name";s:20:"Расфасовка";s:5:"value";s:18:"рассыпной";}i:2;a:2:{s:4:"name";s:10:"Вес, г";s:5:"value";s:3:"100";}i:3;a:2:{s:4:"name";s:23:"Тип упаковки";s:5:"value";s:39:"фольгированный пакет";}i:4;a:2:{s:4:"name";s:39:"Страна производитель";s:5:"value";s:14:"Австрия";}}', 159, 0, 0, 'a:1:{i:0;s:21:"1486109051_JM_17.jpeg";}', 0, 'a:0:{}', 0, NULL, 0, 'Чай зеленый Julius Meinl Солнечная Сенча 100 г', '', '', '2017-02-03 08:03:43', '2017-02-18 10:01:47'),
