(161, 0, 0, 0, 'kofemashina-jura-e6-platin', 'Кофемашина JURA E6 Platin', '15058', 'Jura', '', '<p></p>\r\n<p><span style="color: #b37b40;"><strong><span style="font-size: 18pt;"><span class="word">Кофемашина</span> <span class="word">JURA</span> <span class="word">E6</span> <span class="word">Platin</span></span></strong></span></p>\r\n<p style="text-align: justify;"><br /><span class="word">Новая</span> <span class="word">линия</span> <span class="word">кофемашин</span> <span class="word">Е</span> <span class="word">серии</span> <span class="word">от</span> <span class="word">JURA</span> <span class="word">дарит</span> <span class="word">Вам</span> <span class="word">истинное</span> <span class="word">удовольствие</span> <span class="word">от</span> <span class="word">приготовленного</span> <span class="word">ароматного</span> <span class="word">кофе</span>. <span class="word">Процесс</span> <span class="word">заваривания</span> <span class="word">кофе</span> <span class="word">эспрессо</span> <span class="word">на</span> <span class="word">Jura</span> <span class="word">E6</span> <span class="word">доведен</span> <span class="word">до</span> <span class="word">совершенства</span>. <span class="word">Импульсный</span> <span class="word">процесс</span> <span class="word">экстракции</span> (<span class="word">PEP</span>) <span class="word">является</span> <span class="word">единственным</span> <span class="word">в</span> <span class="word">своем</span> <span class="word">роде</span> <span class="word">процессом</span> <span class="word">в</span> <span class="word">мире</span>. <span class="word">Это</span> <span class="word">ускоряет</span> <span class="word">время</span> <span class="word">экстракции</span> <span class="word">и</span> <span class="word">гарантирует</span> <span class="word">качество</span> <span class="word">напитка</span> <span class="word">сравнимое</span> <span class="word">с</span> <span class="word">качеством</span> <span class="word">лучших</